frappe.ui.form.on("Delivery Note", "refresh", function(frm, cdt, cdn) {
    // code for calculate total and set on parent field.
    total_qty = 0;
    $.each(frm.doc.items || [], function(i, d) {
    total_qty += flt(d.qty);
    });
    frm.set_value("total_qty", total_qty);
    });


frappe.ui.form.on("Delivery Note", "packing_slip_no", function(frm, cdt, cdn) {
	if(frm.doc.packing_slip_no){
	frappe.call({
           method: "frappe.client.get",
            args: {
                name: frm.doc.packing_slip_no,
                doctype: "Packing Slips"
            },
            callback(r) {
                if (r.message) {
			if(r.message.customer!=frm.doc.customer){
			frm.doc.packing_slip_no='';
			refresh_field("packing_slip_no");
			frappe.throw("Packing Slip Customer Is Different")
			}
			if(parseInt(r.message.is_delivery_note)	==1){
			frm.doc.packing_slip_no='';
			refresh_field("packing_slip_no");
				frappe.throw("Already Scanned")

			}
                    for (var row in r.message.items) {
			console.log(row);
                        var child = frm.add_child("items");
                        frappe.model.set_value(child.doctype, child.name, "item_code", r.message.items[row].item_code);
			 frappe.model.set_value(child.doctype, child.name, "qty", r.message.items[row].qty);
			frappe.model.set_value(child.doctype, child.name, "warehouse", r.message.warehouse)
                        refresh_field("items");
			frm.doc.packing_slip_no='';
			refresh_field("packing_slip_no");
      			  frappe.call({
        			    "method": "warehouseapp.api.updatePackingSlip",
            			    "args": {
                                    "name":r.message.name
                                     },
				    callback:function(res){
					}
                               });
                    }

                }
            }

        })
	}
    });

frappe.ui.form.on("Delivery Note", "item_barcode", function(frm, cdt, cdn) {
