				}

			}
			if(flag==0)
			{
                        var child = frm.add_child("items");

                        frappe.model.set_value(child.doctype, child.name, "item_code",item.item_barcode);
                        frappe.model.set_value(child.doctype, child.name, "qty",1);
			refresh_field("items");

			frm.doc.item_barcode='';
			refresh_field("item_barcode");
			cur_frm.save();

					//frappe.model.set_value(cdt,cdn,"barcode",frm.doc.name)
					//refresh_field("barcode");

			}
});

