frappe.ui.form.on(cur_frm.doctype, {
            'onload_post_render': function(frm, cdt, cdn) {
                frappe.ui.keys.on('enter', function(e) {
				var doc_item=locals[cdt][cdn]
                            if (!frm.doc.barcode == '') {
                                var me = this;



                                var brand_name =doc_item.brand;
                                var brand_first = brand_name.charAt(0);
                                var item =doc_item.barcode;
                                var item_code = brand_first + item.substring(2);
				frappe.model.set_value(cdt,cdn,"item_code",item_code.toLowerCase())
				cur_frm.save();
                                var doc =doc_item
                                var doctype = 'Packing';
                                if (!frm.doc.print_format) {
                                    frappe.throw("Please Select Print Format")
                                }
                                frappe.call({
                                        "method": "warehouseapp.api.getItemCode",
                                        args: {
                                            brand: frm.doc.brand,
                                            barcode: frm.doc.barcode,
                                            print_format: frm.doc.print_format
                                        },
                                        callback: function(r, rt) {
                                            if (r.message) {
                                                if (frm.doc.print_format == "Small Barcode") {
                                                    var pf = 'SB'
                                                    var w = window.open(frappe.urllib.get_full_url("/printview?" +
                                                        "doctype=" + "Packing" +
                                                        "&name=" + frm.doc.name +
                                                        "&trigger_print=1" +
                                                        "&format=" + pf +
                                                        "&no_letterhead=0" +
                                                        (me.lang_code ? ("&_lang=" + me.lang_code) : "")));
                                                    if (!w) {
                                                        msgprint(__("Please enable pop-ups for printing."));
                                                        return;
                                                    }
                                                    w.print();
                                                } else if (frm.doc.print_format == "Item Barcode") {
                                                    w = window.open(frappe.urllib.get_full_url("/printview?" +
                                                        "doctype=" + "Packing" +
                                                        "&name=" +frm.doc.name+
                                                        "&trigger_print=1" +
                                                        "&format=" + frm.doc.print_format +
                                                        "&no_letterhead=0" +
                                                        (me.lang_code ? ("&_lang=" + me.lang_code) : "")));
                                                    if (!w) {
                                                        msgprint(__("Please enable pop-ups for printing."));
                                                        return;
                                                    }
                                                    w.print();
                                                } else if (frm.doc.print_format == "90x240") {
                                                    w = window.open(frappe.urllib.get_full_url("/printview?" +
                                                        "doctype=" + "Packing" +
                                                        "&name=" +frm.doc.name+
                                                        "&trigger_print=1" +
                                                        "&format=" + frm.doc.print_format +
                                                        "&no_letterhead=0" +
                                                        (me.lang_code ? ("&_lang=" + me.lang_code) : "")));
                                                    if (!w) {
                                                        msgprint(__("Please enable pop-ups for printing."));
                                                        return;
                                                    }
                                                    w.print();
                                                } else {
                                                    frappe.msgprint("Please Set item_code,brand,barcode in Item Barcode master")
                                                }
                                            }
                                        }
                                })
                            }
                        })
                    },
                    target_warehouse: function(frm, cdt, cdn) {
                        var doc = locals[cdt][cdn]
                        if (doc.target_warehouse) {
                            frappe.call({
                                method: 'warehouseapp.api.getAddressName',
                                args: {
                                    'warehouse': doc.target_warehouse
                                },
                                callback: function(r) {
                                    if (r.message) {
                                        frappe.model.set_value(cdt, cdn, "address", r.message)
                                        cur_frm.save();
                                    }
                                }
                            })
                        }
                    }
            });
